import React, { useState, useEffect } from "react"
import { Input } from 'antd';
import axios from 'axios';
const { Configuration, OpenAIApi } = require("openai")

const { TextArea } = Input;
const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition
const mic = new SpeechRecognition()

mic.continuous = true
mic.interimResults = true
mic.lang = 'th-Th'

const configuration = new Configuration({
    apiKey: "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",
});
const openai = new OpenAIApi(configuration);

const Header = () => {
    const [isListening, setIsListening] = useState(false)
    // const [ip, setIp] = useState('')
    const [text, setText] = useState(null)

    useEffect(() => {
        handleListen();
    }, [isListening])

    const handleListen = () => {
        mic.start()
        mic.onresult = e => {
            console.log(e)
            const transcript = Array.from(e.results)
                .map(res => res[0])
                .map(res => res.transcript)
                .join('')
            setText(transcript)
        }
        mic.stop()
    }

    return (
        <>
            <br /><br />
            <div />
            <Input type="text" id="inputText" onChange={async e => {
                // setText(e.target.value)
                const question = e.target.value
                const response = await openai.createCompletion({
                    model: "text-davinci-003",
                    prompt: `q: ${question}\n a: `,
                    temperature: 0,
                    max_tokens: 100,
                    top_p: 1,
                    frequency_penalty: 0.0,
                    presence_penalty: 0.0,
                    stop: ["\n"],
                })
                setText(response.data.choices?.[0].text)
            }} placeholder="text" value={text} />
            <br /><br />
            <TextArea id="outputText" placeholder="Password" value={text} />
        </>
    )
}

export default Header
